        <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span> <span class="token attr-name">v-for</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>(value,key,index) of userInfo<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
